INSERT OR REPLACE INTO Pelicula (id_pelicula, titulo, descripcion, poster_path, backdrop_path, trailer, anio_estreno, duracion) VALUES (769, 'Uno de los nuestros', 'Henry, un niño de trece años de Brooklyn, vive fascinado con el mundo de los gángsters. Su sueño se hace realidad cuando entra a formar parte de la familia Pauline, dueña absoluta de la zona, que lo educan como un miembro más de la banda convirtiéndole en un destacado mafioso.', 'https://image.tmdb.org/t/p/original/3Yy0zBO9AlyAZH1cTI8Ko2ouCi.jpg', 'https://image.tmdb.org/t/p/original/gILte6Zd7m1YneIr6MVhh30S9pr.jpg', 'https://www.youtube.com/watch?v=Shj-QWYDn_M', 1990, 146);
