INSERT OR REPLACE INTO Pelicula (id_pelicula, titulo, descripcion, poster_path, backdrop_path, trailer, anio_estreno, duracion) VALUES (1218925, 'Chainsaw Man - La película: El arco de Reze', 'Denji trabajaba como cazador de demonios para la yakuza, tratando de saldar la deuda que había heredado de sus padres, pero la yakuza lo traicionó y lo mató. Antes de perder el conocimiento, Pochita, el perro-demonio motosierra de Denji, hizo un trato con él y le salvó la vida. Así se fusionaron, creando al imparable Chainsaw Man. Ahora, en medio de una brutal guerra entre demonios, cazadores y enemigos secretos, una misteriosa chica llamada Reze irrumpe en su mundo y Denji se enfrenta a su batalla más mortífera, impulsado por el amor, en un mundo donde la supervivencia no conoce reglas.', 'https://image.tmdb.org/t/p/original/fJdnp5OO2wh07tdy6noACNyaqMJ.jpg', 'https://image.tmdb.org/t/p/original/gqTz24ZRsCP6AKjARmEivY7m0cK.jpg', 'https://www.youtube.com/watch?v=-KpVet_60Yc', 2025, 100);
