INSERT OR REPLACE INTO Pelicula (id_pelicula, titulo, descripcion, poster_path, backdrop_path, trailer, anio_estreno, duracion) VALUES (207, 'El club de los poetas muertos', 'En un elitista y estricto colegio privado de Nueva Inglaterra, un grupo de alumnos descubrirá la poesía, el significado de "Carpe Diem" -aprovechar el momento- y la importancia de perseguir los sueños, gracias a un excéntrico profesor que despierta sus mentes por medio de métodos poco convencionales.', 'https://image.tmdb.org/t/p/original/vsLcmuk8KBN73spAIEwm1nLNSjc.jpg', 'https://image.tmdb.org/t/p/original/yYvBNN96Ym3wtPxGCFl7F888WwB.jpg', 'https://www.youtube.com/watch?v=S52E2aje2l0', 1989, 128);
