INSERT OR REPLACE INTO Pelicula (id_pelicula, titulo, descripcion, poster_path, backdrop_path, trailer, anio_estreno, duracion) VALUES (281957, 'El renacido', 'Durante una expedición en las tierras salvajes americanas, el legendario explorador Hugh Glass es brutalmente atacado por un oso y dado por muerto por los miembros de su propia partida de caza. En su cruzada por sobrevivir, Glass supera inimaginables tribulaciones, además de la traición de su confidente John Fitzgerald. Guiado por la pura determinación y el amor de su familia, Glass deberá superar un duro invierno mientras busca sin descanso un motivo para seguir adelante y encontrar la redención.', 'https://image.tmdb.org/t/p/original/k3Jh7J5GGMx1lmr7qmynRxOvOK5.jpg', 'https://image.tmdb.org/t/p/original/mNQtUJv1F3u0uSKILFrGjIHqkxx.jpg', 'https://www.youtube.com/watch?v=a8HRA49kaok', 2015, 156);
